{
  "name": "Pasta Carbonara",
  "description": "Classic Italian pasta with creamy sauce made with eggs, bacon, and Parmesan cheese",
  "ingredients": [
    {
      "item": "Spaghetti",
      "quantity": 400,
      "unit": "g"
    },
    {
      "item": "Eggs",
      "quantity": 3,
      "unit": "pieces"
    },
    {
      "item": "Bacon",
      "quantity": 200,
      "unit": "g"
    },
    {
      "item": "Parmesan Cheese",
      "quantity": 100,
      "unit": "g"
    },
    {
      "item": "Black Pepper",
      "quantity": 1,
      "unit": "tsp"
    }
  ],
  "instructions": [
    {
      "step": 1,
      "description": "Bring a large pot of salted water to a boil"
    },
    {
      "step": 2,
      "description": "Add spaghetti and cook until al dente (about 8-10 minutes)"
    },
    {
      "step": 3,
      "description": "Cut bacon into small pieces and fry in a pan until crispy"
    },
    {
      "step": 4,
      "description": "In a bowl, beat eggs with grated Parmesan cheese and black pepper"
    },
    {
      "step": 5,
      "description": "Drain pasta, reserving 1 cup of pasta water"
    },
    {
      "step": 6,
      "description": "Add hot pasta to bacon and mix well"
    },
    {
      "step": 7,
      "description": "Remove from heat and quickly add egg mixture, tossing constantly"
    },
    {
      "step": 8,
      "description": "Add pasta water if needed to achieve creamy consistency"
    }
  ],
  "prepTime": 10,
  "cookTime": 20,
  "servings": 4,
  "difficulty": "Easy",
  "cuisine": "Italian",
  "tags": ["pasta", "quick", "easy", "classic", "italian"],
  "rating": 5
}

{
  "name": "Margherita Pizza",
  "description": "Traditional Italian pizza with fresh mozzarella, basil, and tomato sauce",
  "ingredients": [
    {
      "item": "Pizza Dough",
      "quantity": 500,
      "unit": "g"
    },
    {
      "item": "Tomato Sauce",
      "quantity": 200,
      "unit": "ml"
    },
    {
      "item": "Fresh Mozzarella",
      "quantity": 250,
      "unit": "g"
    },
    {
      "item": "Fresh Basil",
      "quantity": 10,
      "unit": "pieces"
    },
    {
      "item": "Olive Oil",
      "quantity": 2,
      "unit": "tbsp"
    },
    {
      "item": "Salt",
      "quantity": 0.5,
      "unit": "tsp"
    }
  ],
  "instructions": [
    {
      "step": 1,
      "description": "Preheat oven to 250°C (475°F)"
    },
    {
      "step": 2,
      "description": "Roll out pizza dough on a baking stone or sheet"
    },
    {
      "step": 3,
      "description": "Spread tomato sauce evenly over the dough"
    },
    {
      "step": 4,
      "description": "Tear fresh mozzarella and distribute over sauce"
    },
    {
      "step": 5,
      "description": "Drizzle with olive oil and season with salt"
    },
    {
      "step": 6,
      "description": "Bake for 12-15 minutes until crust is golden"
    },
    {
      "step": 7,
      "description": "Add fresh basil immediately after removing from oven"
    }
  ],
  "prepTime": 15,
  "cookTime": 15,
  "servings": 2,
  "difficulty": "Easy",
  "cuisine": "Italian",
  "tags": ["pizza", "vegetarian", "italian", "classic"],
  "rating": 4.8
}

{
  "name": "Chicken Tikka Masala",
  "description": "Creamy and aromatic Indian curry with tender chicken pieces and tomato-cream sauce",
  "ingredients": [
    {
      "item": "Chicken Breast",
      "quantity": 600,
      "unit": "g"
    },
    {
      "item": "Yogurt",
      "quantity": 200,
      "unit": "ml"
    },
    {
      "item": "Tomato Sauce",
      "quantity": 400,
      "unit": "ml"
    },
    {
      "item": "Heavy Cream",
      "quantity": 200,
      "unit": "ml"
    },
    {
      "item": "Ginger Garlic Paste",
      "quantity": 2,
      "unit": "tbsp"
    },
    {
      "item": "Garam Masala",
      "quantity": 1,
      "unit": "tbsp"
    },
    {
      "item": "Turmeric",
      "quantity": 0.5,
      "unit": "tsp"
    },
    {
      "item": "Chili Powder",
      "quantity": 1,
      "unit": "tsp"
    },
    {
      "item": "Onions",
      "quantity": 2,
      "unit": "pieces"
    }
  ],
  "instructions": [
    {
      "step": 1,
      "description": "Marinate chicken in yogurt, ginger-garlic paste, and spices for 30 minutes"
    },
    {
      "step": 2,
      "description": "Grill or pan-fry chicken pieces until cooked through"
    },
    {
      "step": 3,
      "description": "Slice onions thinly and sauté in oil until golden brown"
    },
    {
      "step": 4,
      "description": "Add ginger-garlic paste and cook for 1 minute"
    },
    {
      "step": 5,
      "description": "Add garam masala, turmeric, and chili powder; stir well"
    },
    {
      "step": 6,
      "description": "Add tomato sauce and simmer for 10 minutes"
    },
    {
      "step": 7,
      "description": "Stir in heavy cream and cooked chicken pieces"
    },
    {
      "step": 8,
      "description": "Simmer for 5 minutes until flavors blend well"
    },
    {
      "step": 9,
      "description": "Serve hot with rice or naan bread"
    }
  ],
  "prepTime": 40,
  "cookTime": 35,
  "servings": 4,
  "difficulty": "Medium",
  "cuisine": "Indian",
  "tags": ["curry", "chicken", "indian", "aromatic", "creamy"],
  "rating": 4.9
}
